mapper.select:
		select * from yx_report


                order by ${sidx} ${order}


                order by id desc



			limit #{limit} offset #{offset}

mapper.update:
update yx_report
user_id = #{userId}, task_id = #{taskId}, search_id = #{searchId}, yi_xiang_type = #{yiXiangType}, name = #{name}, id_no = #{idNo}, mobile = #{mobile}, report_json_path = #{reportJsonPath}, report_html_path = #{reportHtmlPath}, verify_status = #{verifyStatus}, is_expired = #{isExpired}, create_time = #{createTime}, update_time = #{updateTime}
where id = #{id}
